import React, { useState } from 'react';

export default function Contact() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        const formData = { name, email, message };

        // Simulating the form submission with a timeout
        setTimeout(() => {
            console.log('Form Data:', formData);
            setIsSubmitting(false);
            setIsSubmitted(true);
        }, 1000);
    };

    return (
        <div className="container">
            <div className="card">
                <div className="contact_title">Let's Connect</div>
                {isSubmitted ? (
                    <div className="success-message">Thank you for your message! We'll be in touch.</div>
                ) : (
                    <form onSubmit={handleSubmit}>
                        <div className="inputBox1">
                            <input
                                type="text"
                                id="name"
                                className="input-field"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                            />
                            <label htmlFor="name" className="input-label">Name</label>
                        </div>

                        <div className="inputBox">
                            <input
                                type="email"
                                id="email"
                                className="input-field"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                            <label htmlFor="email" className="input-label">Email</label>
                        </div>

                        <div className="inputBox">
                            <textarea
                                id="message"
                                className="input-field"
                                value={message}
                                onChange={(e) => setMessage(e.target.value)}
                                required
                            />
                            <label htmlFor="message" className="input-label">Message</label>
                        </div>

                        <button className="enter" type="submit" disabled={isSubmitting}>
                            {isSubmitting ? 'Submitting...' : 'Submit'}
                        </button>
                    </form>
                )}
            </div>
        </div>
    );
}
