import React, { useState } from 'react';

export default function Contact() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission here (e.g., send data to a server or perform some action)
        console.log('Name:', name);
        console.log('Email:', email);
        console.log('Message:', message);
        // You can replace the console.log statements with your desired functionality
        // such as making an API request, sending an email, or storing the data in a database
    };

    return (
        <div className="container">
            <div className="card">
                <div className="contact_title">Let's Connect</div>
                <form onSubmit={handleSubmit}>
                    <div className="inputBox1">
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                        <span className="user">Name</span>
                    </div>

                    <div className="inputBox">
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                        <span>Email</span>
                    </div>

                    <div className="inputBox">
                        <textarea
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            required
                        />
                        <span>Message</span>
                    </div>

                    <button className="enter" type="submit">Submit</button>
                </form>
            </div>
        </div>
    );
}
